import React, { useState } from 'react';
import ThemeToggle from './ThemeToggle';

function Settings({ theme, setTheme }) {
  // Placeholder states for future settings
  const [server, setServer] = useState('ws://localhost:8000');
  const [serverStatus, setServerStatus] = useState('disconnected');
  const [devMode, setDevMode] = useState(false);
  const [logLevel, setLogLevel] = useState('info');

  return (
    <div style={{ maxWidth: 700, margin: '0 auto', padding: '1.5rem 0', display: 'flex', flexDirection: 'column', gap: 32 }}>
      {/* Appearance Section */}
      <section>
        <h2>🎨 Appearance</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: 18 }}>
          <ThemeToggle theme={theme} setTheme={setTheme} />
          <span style={{ color: 'var(--fg)', fontSize: '1em', opacity: 0.7 }}>Toggle dark/light mode</span>
        </div>
      </section>

      {/* Backend Connection Section */}
      <section>
        <h2>🔌 Backend Connection</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16, marginBottom: 10 }}>
          <input
            type="text"
            value={server}
            onChange={e => setServer(e.target.value)}
            style={{ flex: 1, borderRadius: 7, border: '1px solid #333', fontSize: '0.97em', padding: '0.4em 0.8em' }}
            aria-label="Backend server address"
            placeholder="ws://localhost:8000"
          />
          <button style={{ fontSize: '0.97em' }} title="Reconnect (coming soon)">Reconnect</button>
        </div>
        <div style={{ fontSize: '0.97em', color: serverStatus === 'connected' ? '#43e97b' : '#ff9800', fontWeight: 600 }}>
          Status: {serverStatus === 'connected' ? '🟢 Connected' : '🟠 Disconnected'}
        </div>
      </section>

      {/* Data & Privacy Section */}
      <section>
        <h2>🗄️ Data & Privacy</h2>
        <div style={{ display: 'flex', gap: 14, flexWrap: 'wrap' }}>
          <button style={{ fontSize: '0.97em' }} title="Clear app cache (coming soon)">🧹 Clear Cache</button>
          <button style={{ fontSize: '0.97em' }} title="Reset all settings (coming soon)">♻️ Reset App</button>
          <button style={{ fontSize: '0.97em' }} title="Export settings (coming soon)">⬇️ Export Settings</button>
        </div>
      </section>

      {/* Advanced Section */}
      <section>
        <h2>⚙️ Advanced</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: 18, marginBottom: 10 }}>
          <label style={{ display: 'flex', alignItems: 'center', gap: 6, fontWeight: 500 }}>
            <input
              type="checkbox"
              checked={devMode}
              onChange={e => setDevMode(e.target.checked)}
              style={{ accentColor: 'var(--accent)' }}
            />
            Developer Mode
          </label>
          <label style={{ display: 'flex', alignItems: 'center', gap: 6, fontWeight: 500 }}>
            Log Level:
            <select value={logLevel} onChange={e => setLogLevel(e.target.value)} style={{ borderRadius: 7, fontSize: '0.97em' }}>
              <option value="debug">Debug</option>
              <option value="info">Info</option>
              <option value="warn">Warn</option>
              <option value="error">Error</option>
            </select>
          </label>
        </div>
        <div style={{ fontSize: '0.97em', color: '#aaa' }}>Advanced options are for troubleshooting and development. (Placeholders)</div>
      </section>
    </div>
  );
}

export default Settings;
