import React, { useState } from 'react';

const initialModels = [
  { name: 'Mistral 7B', format: 'GGUF', size: '4.2GB', quant: 'Q4_K_M', loaded: true, details: 'A fast, efficient 7B model for local inference.' },
  { name: 'Llama 2 7B', format: 'GGUF', size: '3.9GB', quant: 'Q4_0', loaded: false, details: 'Meta‚Äôs Llama 2, 7B parameter version.' },
];

function Models() {
  const [models, setModels] = useState(initialModels);
  const [selected, setSelected] = useState(models[0]);

  const handleAddModel = () => {
    // Placeholder for file picker or model add dialog
    alert('Add Model (file picker coming soon)');
  };

  const handleSelect = (model) => setSelected(model);

  return (
    <div style={{ maxWidth: 900, margin: '0 auto', padding: '1.5rem 0' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: 18 }}>
        <h2 style={{ flex: 1 }}>üß† Models</h2>
        <button onClick={handleAddModel} style={{ fontWeight: 700, fontSize: '1.01em', boxShadow: '0 2px 8px #ff980033' }}>‚ûï Add Model</button>
      </div>
      <div style={{ display: 'flex', gap: 32, flexWrap: 'wrap' }}>
        {/* Models Table */}
        <div style={{ flex: 2, minWidth: 340 }}>
          <table style={{ width: '100%', borderCollapse: 'separate', borderSpacing: 0, borderRadius: 10, overflow: 'hidden', boxShadow: '0 1px 8px #0002', fontSize: '0.98em' }}>
            <thead>
              <tr style={{ background: '#222', color: '#fff' }}>
                <th>Name</th><th>Format</th><th>Size</th><th>Quant</th><th>Loaded</th><th></th>
              </tr>
            </thead>
            <tbody>
              {models.map((m, i) => (
                <tr
                  key={m.name}
                  style={{ background: selected === m ? 'rgba(255,152,0,0.10)' : m.loaded ? '#e0ffe0' : '#fff', cursor: 'pointer', transition: 'background 0.18s' }}
                  onClick={() => handleSelect(m)}
                >
                  <td style={{ fontWeight: 600 }}>{m.name}</td>
                  <td>{m.format}</td>
                  <td>{m.size}</td>
                  <td>{m.quant}</td>
                  <td>{m.loaded ? '‚úÖ' : '‚ùå'}</td>
                  <td>
                    <button style={{ fontSize: '0.95em', padding: '0.2em 0.7em', marginRight: 6 }} onClick={e => { e.stopPropagation(); alert('Load/Unload coming soon'); }}>{m.loaded ? 'Unload' : 'Load'}</button>
                    <button style={{ fontSize: '0.95em', padding: '0.2em 0.7em' }} onClick={e => { e.stopPropagation(); alert('Delete coming soon'); }}>üóëÔ∏è</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Model Details */}
        <div style={{ flex: 1, minWidth: 240, background: 'rgba(255,255,255,0.04)', borderRadius: 10, boxShadow: '0 1px 8px #0001', padding: '1.1em 1.2em', display: 'flex', flexDirection: 'column', justifyContent: 'center', animation: 'fadeInModelDetails 0.7s' }}>
          <style>{`
            @keyframes fadeInModelDetails {
              from { opacity: 0; transform: translateY(20px); }
              to { opacity: 1; transform: none; }
            }
          `}</style>
          <h3 style={{ margin: 0, marginBottom: 10, fontWeight: 700, color: '#ff9800', fontSize: '1.08em' }}>Model Details</h3>
          {selected ? (
            <>
              <div style={{ fontWeight: 600, fontSize: '1.07em', marginBottom: 6 }}>{selected.name}</div>
              <div style={{ color: '#888', marginBottom: 8 }}>{selected.details}</div>
              <div><b>Format:</b> {selected.format}</div>
              <div><b>Size:</b> {selected.size}</div>
              <div><b>Quant:</b> {selected.quant}</div>
              <div><b>Status:</b> {selected.loaded ? <span style={{ color: 'limegreen' }}>Loaded</span> : <span style={{ color: '#c00' }}>Not loaded</span>}</div>
            </>
          ) : <div style={{ color: '#888' }}>Select a model to see details.</div>}
        </div>
      </div>
    </div>
  );
}

export default Models;
