import React, { useState, useEffect } from 'react';
import PromptInput from './components/PromptInput';
import TokenStream from './components/TokenStream';
import LogitsChart from './components/LogitsChart';
import StatusBar from './components/StatusBar';
import ThemeToggle from './components/ThemeToggle';
import './styles/app.css';

function App() {
  const [theme, setTheme] = useState('dark');
  const [status, setStatus] = useState('disconnected');
  const [model, setModel] = useState('mistral-7b');
  const [inferenceState, setInferenceState] = useState('idle');
  const [error, setError] = useState(null);

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div className="App" style={{ minWidth: 1000, minHeight: 700, overflow: 'hidden', maxWidth: '100vw', maxHeight: '100vh', boxSizing: 'border-box' }}>
      {error && (
        <div style={{ background: '#ffdddd', color: '#900', padding: '0.5em 1em', borderRadius: 6, marginBottom: 12 }}>
          <b>Error:</b> {error}
        </div>
      )}
      <ThemeToggle theme={theme} setTheme={setTheme} />
      <h1>Inferno â€” LLM Debugger</h1>
      <PromptInput />
      <TokenStream />
      <LogitsChart />
      <StatusBar status={status} model={model} state={inferenceState} />
    </div>
  );
}

export default App;
