import React, { useState, useEffect, useRef } from 'react';
import ThemeToggle from './components/ThemeToggle';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import './styles/app.css';

function App() {
  const [theme, setTheme] = useState('dark');
  const [status, setStatus] = useState('disconnected');
  const [model, setModel] = useState('mistral-7b');
  const [inferenceState, setInferenceState] = useState('idle');
  const [error, setError] = useState(null);
  const [activeSection, setActiveSection] = useState('Dashboard');

  // Refs for each dashboard section
  const sectionRefs = {
    Dashboard: useRef(),
    Models: useRef(),
    'Prompt Studio': useRef(),
    Compare: useRef(),
    Sessions: useRef(),
    Settings: useRef(),
    About: useRef(),
  };

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  useEffect(() => {
    // Scroll to the active section when it changes
    if (activeSection && sectionRefs[activeSection] && sectionRefs[activeSection].current) {
      sectionRefs[activeSection].current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [activeSection]);

  return (
    <div style={{ display: 'flex', minWidth: 1000, minHeight: 700, maxWidth: '100vw', maxHeight: '100vh', boxSizing: 'border-box', overflow: 'hidden' }}>
      <Sidebar active={activeSection} setActive={setActiveSection} />
      <div className="App" style={{ flex: 1, overflow: 'auto', paddingLeft: 0 }}>
        {error && (
          <div style={{ background: '#ffdddd', color: '#900', padding: '0.5em 1em', borderRadius: 6, marginBottom: 12 }}>
            <b>Error:</b> {error}
          </div>
        )}
        <ThemeToggle theme={theme} setTheme={setTheme} />
        <Dashboard
          status={status}
          model={model}
          inferenceState={inferenceState}
          sectionRefs={sectionRefs}
        />
      </div>
    </div>
  );
}

export default App;
